// !too slow
func threeSum(nums []int) [][]int {
	ans := [][]int{}
	diff := map[int][]int{}

	for index := 1; index < len(nums); index++ {
		d := 0 - nums[index]
		if len(diff[d]) < 3 {
			diff[d] = append(diff[d], index)
		}
	}

	for i := 0; i < len(nums)-2; i++ {
		for j := i + 1; j < len(nums); j++ {
			if pos, exist := diff[nums[i]+nums[j]]; exist {
				for _, k := range pos {
					if k <= j {
						continue
					}
					t := []int{nums[i], nums[j], nums[k]}
					slices.Sort(t)
					if !slices.ContainsFunc(ans, func(i []int) bool { return slices.Equal(i, t) }) {
						ans = append(ans, t)
						break
					}
				}
			}
		}
	}

	return ans
}

